mapper.select:
SELECT p.id id, p.product_category product_category, p.product_genre product_genre, p.product_name product_name, p.writer_name writer_name, p.register_date register_date, p.update_date update_date, c.file_name file_name, (SELECT COUNT(r.product_id) FROM tbl_product_reply r JOIN tbl_product p ON p.id = r.product_id WHERE p.id = #{id}) reply_cnt
FROM tbl_product p JOIN tbl_product_cover c ON p.id = c.product_id
WHERE p.id = #{id}

SELECT p.id id, p.product_category product_category, p.product_genre product_genre, p.product_name product_name, p.writer_name writer_name, p.register_date register_date, p.update_date update_date, c.file_name file_name
FROM tbl_product p JOIN tbl_product_cover c ON p.id = c.product_id
WHERE p.id = #{id}

SELECT p.id id, p.product_category product_category, p.product_genre product_genre, p.product_name product_name, p.writer_name writer_name, p.register_date register_date, p.update_date update_date, f.file_name file_name
FROM tbl_product p JOIN tbl_product_file f ON p.id = f.product_id
WHERE p.id = #{id}

mapper.update:
UPDATE tbl_product
SET product_category = #{product_category}, product_genre = #{product_genre}, product_name = #{product_name}, writer_name = #{writer_name}, update_date = NOW()
WHERE id = #{id}
